mapper.select:
		select 
      	t1.id as userId, t1.g_id as gId, t1.user_account as userAccount, 
      	t1.user_name as userName, t1.nick_name as nickName, t1.user_type as userType, 
      	t1.gender as gender, t1.status as status, t1.city_code as cityCode, 
      	t1.city_name as cityName, t1.is_delete as isDelete, t1.login_count as loginCount, 
      	t2.id as accountId, t2.diamond as totalDiamond, 
      	t3.id as accountLogId, t3.diamond as diamond, t3.create_time as createTime, t3.source as source, 
      	t3.content as content, t3.type as accountLogType, t3.balance as balance 
        from user_info t1, account t2, account_log t3 
        where t1.id = t2.user_id 
        and t2.id = t3.account_id 
        and t1.is_delete = 0 
        and t3.status = 1 

            and t1.id = #{userId,jdbcType=INTEGER}


            and t1.user_account = #{userAccount,jdbcType=VARCHAR}


            and t1.user_name = #{userName,jdbcType=VARCHAR}


            and t1.nick_name = #{nickName,jdbcType=VARCHAR}


            and t3.type = #{accountLogType,jdbcType=INTEGER}


            and t3.create_time BETWEEN #{timeStartStr,jdbcType=VARCHAR} AND #{timeEndStr,jdbcType=VARCHAR} 


            order by ${orderByClause}

		select 
      	t1.id as userId, t1.g_id as gId, t1.user_account as userAccount, 
      	t1.user_name as userName, t1.nick_name as nickName, t1.user_type as userType, 
      	t1.gender as gender, t1.status as status, t1.city_code as cityCode, 
      	t1.city_name as cityName, t1.is_delete as isDelete, t1.login_count as loginCount, 
      	t2.id as accountCalculateId, t2.calculate as totalCalculate, 
      	t2.civilize_calculate as civilizeCalculate, t2.task_calculate as taskCalculate,
      	t3.id as accountCalculateLogId , 
      	t3.calculate as calculate, t3.content as content, t3.type as accountCalculateLogType, 
      	t3.calculate_type as calculateType, t3.balance as balance, t3.create_time as createTime 
        from user_info t1, account_calculate t2, account_calculate_log t3 
        where t1.id = t2.user_id 
        and t2.id = t3.account_calculate_id
        and t1.is_delete = 0 
        and t3.status = 1 

            and t1.id = #{userId,jdbcType=INTEGER}


            and t1.user_account = #{userAccount,jdbcType=VARCHAR}


            and t1.user_name = #{userName,jdbcType=VARCHAR}


            and t1.nick_name = #{nickName,jdbcType=VARCHAR}


            and t3.type = #{accountCalculateLogType,jdbcType=INTEGER}


            and t3.calculate_type = #{calculateType,jdbcType=INTEGER}


            and t3.create_time BETWEEN #{timeStartStr,jdbcType=VARCHAR} AND #{timeEndStr,jdbcType=VARCHAR} 


            order by ${orderByClause}

		select 
      	t1.id as userId, t1.user_account as userAccount, t1.nick_name as nickName,
      	t1.gender as gender, t1.status as status, t1.city_code as cityCode, 
      	t1.city_name as cityName, t1.login_count as loginCount, 
      	t2.id as accountCalculateId, t2.calculate as totalCalculate, 
      	t2.civilize_calculate as civilizeCalculate, t2.task_calculate as taskCalculate, t2.update_time as updateTime 
        from user_info t1, account_calculate t2
        where t1.id = t2.user_id 
        and t1.is_delete = 0 
        and t3.status = 1 

            and t1.id = #{userId,jdbcType=INTEGER}


            and t1.user_account = #{userAccount,jdbcType=VARCHAR}


            and t1.user_name = #{userName,jdbcType=VARCHAR}


            and t1.nick_name = #{nickName,jdbcType=VARCHAR}


            and t2.update_time BETWEEN #{timeStartStr,jdbcType=VARCHAR} AND #{timeEndStr,jdbcType=VARCHAR} 


            order by ${orderByClause}
