mapper.select:
		select 
      	t1.id as couponLogId, t1.user_id as userId, t1.coupon_id as couponId, t1.user_account as userAccount, 
      	t1.url as url, t1.price as price, t1.coupon_type as couponType, t1.coupon_time as couponTime, 
      	t1.diamond as diamond, t1.status as status, t1.amount as amount, 
      	t3.id as gCouponId, t3.g_name as gName, t3.g_city_name as gCityName, 
      	t3.g_area_info as gAreaInfo 
        from coupon_log t1, coupon_g_coupon t2, g_coupon t3 
        where t1.coupon_id = t2.coupon_id 
        and t2.g_coupon_id = t3.id 

            and t1.coupon_id = #{couponId,jdbcType=INTEGER}


            and t1.user_account = #{userAccount,jdbcType=VARCHAR}


            and t1.status = #{status,jdbcType=INTEGER}


        		and t1.coupon_type = #{couponType,jdbcType=INTEGER}


            and t1.coupon_time BETWEEN #{couponTimeStartStr,jdbcType=VARCHAR} AND #{couponTimeEndStr,jdbcType=VARCHAR} 


            and t3.id = #{gCouponId,jdbcType=VARCHAR}


            and t3.g_name = #{gName,jdbcType=VARCHAR}


            order by ${orderByClause}
