mapper.select:
		select 
      	t1.id as couponId, t1.url as url, t1.amount as amount, t1.diamond as diamond, 
      	t1.is_use as isUse, t1.create_time as createTime, t1.update_time as updateTime, 
      	t3.id as gCouponId, t3.g_name as gName, t3.g_city_name as gCityName, 
      	t3.g_area_info as gAreaInfo, t3.g_price_type as couponType, t3.g_money as price 
        from coupon t1, coupon_g_coupon t2, g_coupon t3 
        where t1.id = t2.coupon_id 
        and t2.g_coupon_id = t3.id 
        and t1.is_delete = 0 

            and t1.id = #{couponId,jdbcType=INTEGER}


            and t1.amount = #{amount,jdbcType=INTEGER}


            and t1.is_use = #{isUse,jdbcType=INTEGER}


            and t3.id = #{gCouponId,jdbcType=VARCHAR}


        		and t3.g_price_type = #{couponType,jdbcType=INTEGER}


      		order by ${orderByClause}
