mapper.select:
  select 
  t1.id as calculateConfigId, t1.calculate_code as calculateCode, t1.calculate_name as calculateName, t1.calculate_type as calculateType, t1.content as content, 
	t1.is_use as isUse, t1.url as url, t1.is_skip as isSkip, t1.is_delete as isDelete, t1.remark as remark, 
	t2.id as userCalculateConfigId, t2.complete_time as complete_time, IFNULL(t2.is_complete, 0)
  from calculate_config t1 left join user_calculate_config t2 on t1.calculate_code = t2.calculate_code 
  where t1.is_use = 1 
  and t1.is_delete = 0

  	and t2.user_id = #{userId,jdbcType=INTEGER} 


  	and t1.calculate_type = #{calculateType,jdbcType=INTEGER} 


		order by ${orderByClause}
