mapper.select:
select t1.id as userQualityId, t1.enterprise_id as enterpriseId, t1.user_id as userId, t1.user_name as userName, t1.quality_name as qualityName, t1.is_delete as isDelete, t1.remark as remark, t1.created as created, t1.updated as updated, t1.create_time as createTime, t1.update_time as updateTime, t2.id as userQualityAttachmentId, t2.name as name, t2.url as url from user_quality t1, user_quality_attachment t2 where t1.id = t2.user_quality_id and t1.is_delete = 0
and t1.enterprise_id = #{enterpriseId,jdbcType=INTEGER}
order by ${orderByClause}

mapper.update:
update user_quality
set user_name = #{userName,jdbcType=VARCHAR}
where user_id = #{userId,jdbcType=INTEGER}
